function resizeCanvas(canvas) {
    var ratio =  Math.max(window.devicePixelRatio || 1, 1);
    canvas.width = canvas.offsetWidth * ratio;
    canvas.height = canvas.offsetHeight * ratio;
    canvas.getContext("2d").scale(ratio, ratio);
}

$(document).ready(function() {
    var canvas = document.querySelector("canvas");
    if (canvas){
        canvas.height = canvas.offsetHeight;
        canvas.width = canvas.offsetWidth;
        window.onresize = resizeCanvas(canvas);
        resizeCanvas(canvas);
        signature_pad = new SignaturePad(canvas);
        $('#signature-pad-clear').click(function() { signature_pad.clear() });
        $('#signature-pad-save').click(function(event) {
            if (signature_pad.isEmpty()){
                alert('You must draw your signature to save');
                event.preventDefault();
            } else {
                $('#signature-pad-input').val(signature_pad.toDataURL());
                var data = signature_pad.toDataURL('image/png');
                $('#signature-preview').attr('src',data);
                console.log(data);
                $.ajax({
                    url: "/users/update_data",
                    type: "POST",
                    data: {"image_data" : data},
                    success: function(updated_data) {
                    }
                });
            }
        });
    }
});
