$(document).ready(function() {
    $('#logo-file-upload').on('change', function(event) {
        var files = event.target.files;
        var image = files[0]
        // const file = document.getElementById('logo-file-upload').files[0];
        if (image) {
            var img = new Image();
            img.src = file.target.result;
            document.getElementById('logo-preview').src = URL.createObjectURL(image);
        }
    });
});

$(document).ready(function() {
    $('#picture-input').on('change', function(event) {
        var files = event.target.files;
        var image = files[0]
        var reader = new FileReader();
        reader.onload = function(file) {
            var img = new Image();
            img.src = file.target.result;
            $('#target').html(img);
        }
        reader.readAsDataURL(image);
    });
});
